from Algo_gen.GA import *
import argparse
from os import popen
from re import A
from Algo_MTRS.montecarlo_fonctionarbre import *

parser = argparse.ArgumentParser()
parser.add_argument("--filename", help="input filename of DNA sequence")
parser.parse_args()
args = parser.parse_args()

# Command pour running with a file : python main.py --filename=plasmid_8k.fasta

def main(p=2):
    # Running of genetics algorithm
    if p==1:
        seq=""
        algo=GA(40,100,seq,5,args)
        algo.darwin()

    # Running the Monte Carlo algorithm
    else: 
        
        # Read file
        lineList = [line.rstrip('\n') for line in open(args.filename)]
        # Formatting
        seq = ''.join(lineList[1:])
        a=MCTS(10,seq)
        a.draw()
        

if __name__ == "__main__" :
    main()